.section-container {
  /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
  padding-bottom: 1rem;
}

.logo {
  font-size: 1.5rem;
  font-weight: 600;
  line-height: 1.2;
}

.desktop-nav {
  display: none;
  align-items: center;
  justify-content: space-between;
  width: 100%;

  nav {
    display: flex;
    gap: 2rem;

    a.link {
      /* Defined in Site CSS */
      border-color: transparent;
      transition: border-color 0.1s;
      font-size: 0.95rem;

      &:hover {
        border-color: initial;
      }
    }
  }

  @media (min-width: 800px) {
    display: flex;
  }
}

.mobile-nav {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  font-weight: 500;

  @media (min-width: 800px) {
    display: none;
  }
}

/* Mobile navigation popup */
#popup {
  font-size: 1.25rem;
  display: grid;
  gap: 1rem;
  position: absolute;
  text-align: center;
  background-color: var(--background-color, white);
  box-shadow: var(--box-shadow, 0px 4px 30px rgba(0, 0, 0, 0.1));
  border-radius: var(--border-radius);
  inset: 0.5rem;
  bottom: initial;
  padding: 2rem;
  z-index: 1;

  button#close {
    position: absolute;
    right: 1rem;
    top: 1rem;
  }
}

.section-container { /* Defined in Site CSS */
  display: flex;
  justify-content: space-between;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding-top: 1rem;
  padding-bottom: 1rem;

  @media (min-width: 600px) {
    flex-direction: row;
  }
}

nav {
  display: flex;
  gap: 1rem;
  font-size:.875rem;

  .link { /* Defined in Site CSS */
    border-color: transparent;

    &:hover {
      border-color: initial;
    }
  }
}

.primo {
  font-weight: 600;
  font-size: 0.75rem;

  a {
    text-decoration: underline; 
  }
}

.social-links {
  display: flex;
  gap: 1.5rem;
  font-size: 1.5rem;

  a {
    color: var(--color-accent, rebeccapurple);
    transition: var(--transition, 0.1s transform);

    &:hover {
      transform: scale(1.1);
    }
  }
}

.content {
  :global(img) {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  :global(p) {
    padding: 0.5rem 0;
    line-height: 1.5;
  }

  :global(a) {
    text-decoration: underline;
  }

  :global(h1) {
    font-size: 2.5rem;
    font-weight: 600;
    line-height: 1.2;
    margin-bottom: 0.5rem;
  }

  :global(h2) {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
    line-height: 1.2;
  }

  :global(h3) {
    font-size: 1.5rem;
    font-weight: 600;
    line-height: 1.2;
    margin-top: .5rem;
  }

  :global(ul) {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  :global(ol) {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  :global(blockquote) {
    padding: 2rem;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }
}
